CREATE TABLE `story_option` (
  `condition_value` int DEFAULT NULL,
  `effect_value` int DEFAULT NULL,
  `from_node_id` bigint DEFAULT NULL,
  `id` bigint NOT NULL AUTO_INCREMENT,
  `target_node_id` bigint DEFAULT NULL,
  `button_text` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `condition_type` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `effect_type` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=358 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

